// ==UserScript==
// @name        GUI
// @namespace   SMCTools
// @version     2.22
// @description Displays information about products in SMC product repository
// @grant       none
// @include     http://smc*
// @require     http://code.jquery.com/jquery-3.2.1.min.js
// @run-at      document-idle
// @downloadURL https://github.com/br-smc/userscripts/raw/master/SMC_GUI.user.js
// @updateURL https://github.com/br-smc/userscripts/raw/master/SMC_GUI.user.js
// ==/UserScript==

var GUI = window.GUI || {};

GUI.MAIN = (function(){
	return {
		Format: function() {
			$('head').append(`
				<style type="text/css">
					.smc-doc, .title_ct_icon.doc, .smc-media-image-container.doc {
						background-image: url("data:image/svg+xml;base64,") !important;
					}
				</style>`
			);
		}
	};
})();

GUI.ExplorerPaginator = (function(){
	return {
		Format: function() {
			$('head').append(`
				<style type='text/css'>
					.paginator-folder {
						margin-top: 4px;
						margin-right: 2px;
					}
					.paginator-folder .page,
					.paginator-folder .page-selected,
					.paginator-folder .page-arrow,
					.paginator-folder .page-arrow-disabled {
						background-color: #e0e0ff;
						color: #8080ff;
						border-style:  outset;
						border-radius: 4px;
						padding-top: 2px;
						padding-bottom: 2px;
					}
					.paginator-folder .page-selected {
						background-color: #d0d0ff;
						color: #6060ff;
					}
					.paginator-folder .page-arrow-disabled {
						background-color: #e7e7e7;
						color: #b0b0b0;
					}
					.paginator-folder .page:hover,
					.paginator-folder .page-arrow:hover {
						border-color: #8080ff;
						border-style: solid;
					}
				</style>`
			);
		}
	};
})();


$(document).ready(function(){
  setTimeout(function(){
    GUI.MAIN.Format();
    GUI.ExplorerPaginator.Format();
  }, 1000);
});
